const webuxlanguage = require("../index");
const { Webux } = require("webux-app").CreateApp();

const options = {
  availables: ["fr", "en"],
  directory: "locales",
  default: "en",
  autoReload: true,
  syncFiles: true
};

webuxlanguage.init(options);

console.log(webuxlanguage.getLocale());

console.log("Language initialized");

webuxlanguage.setLocale({ headers: { lang: "fr" } });

console.log(webuxlanguage.getLocale());
